function datafilter(obj,query,properties){for(let prop of properties)if(obj[prop]&&obj[prop].toLowerCase().includes(query.toLowerCase()))return!0;return!1}function dataTableValues(){return{limit:"10",page:0,maxpage:0,from:1,to:10,q:""}}function dataTable(listado,{page:page,maxpage:maxpage,from:from,to:to,limit:limit,q:q,temp:temp},way,...properties){if(0==way)maxpage=(temp=listado.filter(item=>datafilter(item,q,properties))).length%limit==0?temp.length/limit:Math.ceil(temp.length/limit);else{if(page+way<0||page+way>=maxpage)return{page:page,maxpage:maxpage,from:from,to:to,limit:limit,temp:temp};to=(from=0==(page+=way)?1:page*limit+1)+parseInt(limit)-1}return{page:page,maxpage:maxpage,from:from=from<(to=temp.length<to?temp.length:limit*(page+1))?from:to<limit?1:to/limit*limit+1,to:to,limit:limit,temp:temp,sublist:temp.slice(from-1,to)}}